import React from 'react';
import './SearchBar.css'


class SearchBar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      term: ''
    }

    this.search = this.search.bind(this);
    this.handleTermChange = this.handleTermChange.bind(this);
  }

  search() {
    this.props.onSearch(this.state.term); //the state of the .term. is passed to .onSearch.
  }

  handleTermChange(event) {
    this.setState({ term: event.target.value }); //Sets the state of the search bar’s term to the event target’s value.
  }

  render() {
    return(
      <div className="SearchBar">
        <input onChange={this.handleTermChange} placeholder="Enter A Song, Album, or Artist" />
        <button className="SearchButton" on>SEARCH</button>
      </div>
    )
  }
}

export default SearchBar;
